package buttons;import java.awt.Component;import java.awt.FlowLayout;import java.util.ListIterator;import javax.swing.JFrame;import javax.swing.JPanel;public class ButtonTest {    public static void main(String[] args)    {        JFrame window = new JFrame();        JPanel controlPanel = new JPanel();        ListIterator<Component> iterator = new ChooseButtons().showButtons().listIterator();        controlPanel.setLayout(new FlowLayout());        window.setSize(400, 400);        while(iterator.hasNext())        {            controlPanel.add(iterator.next());        }        window.add(controlPanel);        window.setVisible(true);    }}